import React, { useState, useEffect, useRef } from "react";
import axios from 'axios'
import {useHistory} from "react-router-dom" 

export default Login = ({setLoginUser}) => {

    const history = useHistory()
    const [user,setUser] = useState({
        username:"",
        password: ""
    })
    const handleChange = e =>{
    const {username,value} = e.target

    setUser({ ...user, [username]:value })
    }

    const OnClickLogin = () => {

        const credentials = {
            userusername: "admin123",
            password: "admin123"
        }
            const res =  axios.post('http://localhost:3000/login/',credentials)

            console.log(res.data);
            console.log('login cliked!')
            setLoginUser(res.data.user)
            // localStorage.setItem(credentials)

            history.push('/home')

    }






        return (
            <div classusername='ui container'>
                <h3>B2 Connect  Login</h3>
                <form action="/login" classusername="ui form" style={{ textAlign: 'center' }}>
                    <div>

                        <label>Login credentials below</label>
                    </div>
                    <div classusername="field" >
                        <input placeholder="Userusername" style={{ width: '300px' }} onChange={handleChange} />
                    </div>
                    <div classusername="field">
                        <input placeholder="Password" type="password" style={{ width: '300px' }}  onChange={handleChange}/></div>
                    <div classusername="field">
                    </div><button classusername="ui button" type="button" onClick={OnClickLogin}>Sign In</button>
                </form>
            </div>
        )
}
