import React, { useState, useEffect, useRef } from "react";
import axios from 'axios'
import {useHistory} from "react-router-dom" 

export default Login = ({setLoginUser}) => {

    const history = useHistory()
    const [user,setUser] = useState({
        name:"",
        password: ""
    })
    const handleChange = e =>{
    const {name,value} = e.target
    setUser({
    ...user,//spread operator 
    [name]:value
    })
    }

    const OnClickLogin = () => {

        const credentials = {
            username: "admin123",
            password: "admin123"
        }
            const res =  axios.post('http://localhost:3000/login/',credentials)

            console.log(res.data);
            console.log('login cliked!')
            setLoginUser(res.data.user)
            // localStorage.setItem(credentials)

            history.push('/home')

    }






        return (
            <div className='ui container'>
                <h3>B2 Connect  Login</h3>
                <form action="/login" className="ui form" style={{ textAlign: 'center' }}>
                    <div>

                        <label>Login credentials below</label>
                    </div>
                    <div className="field" >
                        <input placeholder="Username" style={{ width: '300px' }} />
                    </div>
                    <div className="field">
                        <input placeholder="Password" type="password" style={{ width: '300px' }} /></div>
                    <div className="field">
                    </div><button className="ui button" type="button" onClick={this.OnClickLogin}>Sign In</button>
                </form>
            </div>
        )
}
