import React, { useState } from "react";
import axios from 'axios'
import { useHistory } from "react-router-dom";

const Login = ({setLoginUser}) => {

    const history = useHistory();
    const [user, setUser] = useState({
        username: "",
        password: ""
    })
    const handleChange = e => {
        const { username, value } = e.target

        setUser({ ...user, [username]: value })
    }

    const OnClickLogin = async () => {

        console.log('Username: ' + user.username);
        console.log('Password: ' + user.password);

        // const credentials = {
        //     username: "admin123",
        //     password: "admin123"
        // }
        const res = await axios.post('http://localhost:3000/login/', user)

        console.log(res);
        console.log('login cliked!')
        setLoginUser(res)
        // localStorage.setItem(credentials)

        history.push('/home')

    }






    return (
        <div className='ui container'>
            <h3>B2 Connect  Login</h3>
            <form action="/home" className="ui form" style={{ textAlign: 'center' }}>
                <div>

                    <label>Login credentials below</label>
                </div>
                <div className="field" >
                    <input placeholder="Username" style={{ width: '300px' }} />
                </div>
                <div className="field">
                    <input placeholder="Password" type="password" style={{ width: '300px' }} onChange={handleChange} /></div>
                <div className="field">
                </div><button className="ui button" type="submit" onClick={OnClickLogin}>Sign In</button>
            </form>
        </div>
    )
}

export default Login;
